import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Tooltip,
  Legend,
} from 'chart.js';
import { Chart } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Tooltip,
  Legend
);

const labels = ['01', '02', '03', '04', '05', '06', '07', '08', '09', '10', '11', '12', '13', '14', '15'];

const colors = {
  '18 - 24': '#4DB4FF',
  '25 - 34': '#F4D548',
  '35 - 44': '#EB4D4B',
  '45 +': '#28B463',
};

const ageGroups = {
  '18 - 24': [19, 20, 23, 21, 22, 22, 18, 20, 21, 20, 18, 19, 20, 22, 21],
  '25 - 34': [28, 31, 25, 25, 28, 29, 31, 33, 32, 32, 29, 24, 33, 33, 33],
  '35 - 44': [47, 38, 39, 41, 43, 41, 41, 41, 37, 36, 38, 36, 39, 41, 41],
  '45 +':    [51, 48, 51, 48, 48, 49, 49, 41, 52, 52, 48, 45, 46, 45, 45],
};

// Bar datasets
const barDatasets = Object.keys(ageGroups).map((group) => ({
  type: 'bar',
  label: group,
  data: ageGroups[group],
  backgroundColor: colors[group],
  borderColor: colors[group],
  borderWidth: 2,
  barThickness: 4,
}));

// Scatter point datasets (for circles)
const scatterDatasets = Object.keys(ageGroups).map((group) => ({
  type: 'scatter',
  label: `${group} points`,
  data: ageGroups[group].map((y, i) => ({ x: labels[i], y })),
  backgroundColor: colors[group],
  pointRadius: 7,
  pointHoverRadius: 7,
  showLine: false,
  borderWidth: 0,
}));

const data = {
  labels,
  datasets: [...barDatasets, ...scatterDatasets],
};

const options = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      position: 'top',
      labels: {
        usePointStyle: true,
        pointStyle: 'circle',
        padding: 20,
        filter: (legendItem) => !legendItem.text.includes('points'), // Hide scatter legends
      },
    },
    tooltip: {
      mode: 'index',
      intersect: false,
    },
  },
  scales: {
    x: {
      stacked: true,
      grid: {
        display: false,
      },
    },
    y: {
      beginAtZero: true,
      ticks: {
        stepSize: 10,
      },
    },
  },
};

const AttendeeAgeChart = () => (
  <div style={{ width: '100%', height: '400px' }}>
    <Chart type='bar' data={data} options={options} />
  </div>
);

export default AttendeeAgeChart;
