import React, { useState,  useEffect, useContext } from "react";
import styles from "./reportCard.module.css"; // Import the CSS module

var tmslogo = "/images/logo.png";
var logoDark = "./images/right_choice.png";
var logoLight = "./images/right_choice.png";

function ReportCard() {
  const [login, setLogin] = useState(false);
  const [isLogin, setIsLogin] = useState(false);
  const { showReportCard, setShowReportCard } = useContext(MyContext);

  const showLogin = () => {
    setLogin(true);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    setIsLogin(true);
    setLogin(false);
  };

  return (
    <main className={styles.container}>
      {/* <img src={tmslogo} alt="Logo" className={styles.logo} /> */}

      {login && 
        <div className={styles.loginFormWrapper}>
          <div className={styles.loginForm}>
            <div className={styles.loginButtons}>
              <button onClick={() => setLogin(false)} className="cursor-pointer">sign in</button>
              <button onClick={() => setLogin(false)} className="cursor-pointer">sign up</button>
            </div>
            <form className="flex gap-[20px] flex-col items-center" onSubmit={handleLogin}>
              <input type="email" placeholder="email" className={styles.input} />
              <input type="password" placeholder="Password" className={styles.input} />
              <button type="submit" className={styles.submitButton}>Submit</button>
            </form>
          </div>
        </div>
      }

      <div className={styles.header}>
        Report Card Generator
      </div>

      <div className={`${styles.buttonContainer} sm:flex-row`}>
        {/* Download Template Button */}
        <button
          onClick={() => handleDownloadTemplate(showLogin, isLogin)}
          className={`${styles.button} ${styles.downloadButton}`}
        >
          Download Template
        </button>

        {/* Upload Filled Excel Button */}
        <label
          htmlFor="upload"
          className={`${styles.button} ${styles.uploadButton}`}
        >
          Upload Filled Excel
          <input
            id="upload"
            type="file"
            accept=".xlsx, .xls"
            onChange={handleUpload}
            className="hidden"
          />
        </label>

        {/* Download Results Button */}
        <button
          onClick={handleDownloadResults}
          className={`${styles.button} ${styles.resultsButton}`}
        >
          Download Results
        </button>
      </div>
    </main>
  );
}

const handleDownloadTemplate = (showLogin, isLogin) => {
  if (!isLogin) {
    console.log("not logged in");
    showLogin();
    return;
  }
  const link = document.createElement("a");
  link.href = "/template.xlsx"; // Replace with the actual path to your template
  link.download = "template.xlsx";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

const handleUpload = (event) => {
  const file = event.target.files?.[0];
  if (file) {
    const reader = new FileReader();
    reader.onload = (e) => {
      const data = e.target?.result;
      console.log(data);
    };
    reader.readAsArrayBuffer(file);
  }
};

const handleDownloadResults = () => {
  const link = document.createElement("a");
  link.href = "/path/to/results.xlsx"; // Replace with the actual path to your results
  link.download = "results.xlsx";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

export default ReportCard;
