import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Legend,
  Tooltip,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  Tooltip,
  Legend
);

const labels = ['01', '02', '03', '04', '05', '06', '07', '08', '09', '10', '11', '12', '13', '14', '15'];

const datasetTemplate = (label, data, color) => ({
  label,
  data,
  backgroundColor: color,
  borderColor: color,
  borderWidth: 3,
  borderRadius: 100,
  barThickness: 6,
});

const data = {
  labels,
  datasets: [
    datasetTemplate('18 - 24', [19, 20, 23, 21, 22, 22, 18, 20, 21, 20, 18, 19, 20, 22, 21], '#4DB4FF'),
    datasetTemplate('25 - 34', [28, 31, 25, 25, 28, 29, 31, 33, 32, 32, 29, 24, 33, 33, 33], '#F4D548'),
    datasetTemplate('45 +',   [51, 48, 51, 48, 48, 49, 49, 41, 52, 52, 48, 45, 46, 45, 45], '#28B463'),
  ],
};

const options = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      position: 'top',
      labels: {
        usePointStyle: true,
        pointStyle: 'circle',
        padding: 20,
      },
    },
    tooltip: {
      mode: 'index',
      intersect: false,
    },
  },
  scales: {
    x: {
      stacked: true,
      grid: {
        display: false,
      },
    },
    y: {
      beginAtZero: true,
      ticks: {
        stepSize: 10,
      },
    },
  },
};

const AttendeeAgeChart = () => (
  <div style={{ width: '100%', height: '400px' }}>
    <Bar data={data} options={options} />
  </div>
);

export default AttendeeAgeChart;
