import React, { useState } from "react";
import logo from "/images/logo.png"

var tmslogo = "/images/logo.png";

var logoDark = "./images/right_choice.png";
var logoLight = "./images/right_choice.png";

// const [login, setLogin] = useState(false);


function ReportCard() {
  const [login, setLogin] = useState(false);
  const [isLogin, setIsLogin] = useState(false);

  const showLogin = () => {
    setLogin(true);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    // Logic to handle login
    setIsLogin(true);
    setLogin(false);
  };
  return (
    <main className="flex flex-col items-center pt-16 pb-4 bg-white h-[100vh]">
      <img src={tmslogo} alt="Logo" className="h-[10vw] max-h-[80px]" />
      {login && 
      <div className="flex flex-col items-center gap-[40px] text-pink-500 w-[90vw] min-h-[90vh]  fixed top-[5vh] z-10 p-6 opacity-90 bg-orange-50">
        <div className="bg-green-500 w-[80vw] max-w-[300px] mt-[30px] min-h-[40vh] rounded-[10px] p-6">
          <div className="flex gap-[30px] justify-center mb-[70px] text-white font-bold text-[20px]">
            <button onClick={() => setLogin(false)} className="cursor-pointer">sign in</button>
            <button onClick={() => setLogin(false)} className="cursor-pointer">sign up</button>
          </div>
          <form className="flex gap-[20px] flex-col items-center" onSubmit={handleLogin}>
            <input type="email" placeholder="email" className="text-center text-white border-2 border-white" />
            <input type="password" placeholder="Password" className="text-center text-white border-2 border-white" />
            <button type="submit" className="text-white font-bold cursor-pointer" >Submit</button>
          </form>
        </div>
      </div>}
      <div className="flex text-center justify-center font-bold text-blue-500 text-[40px] ">
        Report Card Generator
      </div>
      <div className="flex sm:flex-row flex-col sm:space-x-4  w-[70vw] justify-center rounded-[10px]  bg-pink-50" >
        {/* Download Template Button */}
        <button
          onClick={() => handleDownloadTemplate( showLogin, isLogin)}
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold p-4 m-4 rounded mh-[50px]"
        >
          Download Template
        </button>

        {/* Upload Filled Excel Button */}
        <label
          htmlFor="upload"
          className="bg-green-500 hover:bg-green-600 text-white font-bold p-4 m-4 rounded mh-[50px] cursor-pointer text-center"
        >
          Upload Filled Excel
          <input
            id="upload"
            type="file"
            accept=".xlsx, .xls"
            onChange={handleUpload}
            className="hidden"
          />
        </label>

        {/* Download Results Button */}
        <button
          onClick={handleDownloadResults}
          className="bg-purple-500 hover:bg-purple-600 text-white font-bold p-4 m-4 rounded mh-[50px] "
        >
          Download Results
        </button>
      </div>
    </main>
  );
}


const handleDownloadTemplate = (showLogin, isLogin) => {
  // const isLogin = checkLogin();

if(!isLogin){
  console.log("not logged in");
  showLogin();
  return;
}
  const link = document.createElement("a");
  link.href = "/template.xlsx"; // Replace with the actual path to your template
  link.download = "template.xlsx";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);

  // if(islogin){
  //   console.log("i loged")
  // }

  // Logic to download the template

}
const handleUpload = (event) => {
  const file = event.target.files?.[0];
  if (file) {
    const reader = new FileReader();
    reader.onload = (e) => {
      const data = e.target?.result;
      // Logic to handle the uploaded file data
      console.log(data);
    };
    reader.readAsArrayBuffer(file);
  }
}
const handleDownloadResults = () => {
  // Logic to download the results
  const link = document.createElement("a");
  link.href = "/path/to/results.xlsx"; // Replace with the actual path to your results
  link.download = "results.xlsx";
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}

export default ReportCard;
