import Chart from 'chart.js/auto';

const ctx = document.getElementById('attendeeAgeChart').getContext('2d');

const data = {
  labels: ['01', '02', '03', '04', '05', '06', '07', '08', '09', '10', '11', '12', '13', '14', '15'],
  datasets: [
    {
      label: '18 - 24',
      data: [19, 20, 23, 21, 22, 22, 18, 20, 21, 20, 18, 19, 20, 22, 21],
      backgroundColor: '#4DB4FF',
      borderColor: '#4DB4FF',
      borderWidth: 3,
      pointRadius: 6,
      pointBackgroundColor: '#4DB4FF',
      pointStyle: 'circle',
      type: 'line',
    },
    {
      label: '25 - 34',
      data: [28, 31, 25, 25, 28, 29, 31, 33, 32, 32, 29, 24, 33, 33, 33],
      backgroundColor: '#F4D548',
      borderColor: '#F4D548',
      borderWidth: 3,
      pointRadius: 6,
      pointBackgroundColor: '#F4D548',
      pointStyle: 'circle',
      type: 'line',
    },
    {
      label: '35 - 44',
      data: [47, 38, 39, 41, 43, 41, 41, 41, 37, 36, 38, 36, 39, 41, 41],
      backgroundColor: '#EB4D4B',
      borderColor: '#EB4D4B',
      borderWidth: 3,
      pointRadius: 6,
      pointBackgroundColor: '#EB4D4B',
      pointStyle: 'circle',
      type: 'line',
    },
    {
      label: '45+',
      data: [51, 48, 51, 48, 48, 49, 49, 41, 52, 52, 48, 45, 46, 45, 45],
      backgroundColor: '#28B463',
      borderColor: '#28B463',
      borderWidth: 3,
      pointRadius: 6,
      pointBackgroundColor: '#28B463',
      pointStyle: 'circle',
      type: 'line',
    },
  ],
};

const options = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      position: 'top',
      labels: {
        usePointStyle: true,
        pointStyle: 'circle',
        padding: 20,
      },
    },
    tooltip: {
      mode: 'index',
      intersect: false,
    },
  },
  scales: {
    x: {
      title: { display: false },
      grid: { display: false },
    },
    y: {
      beginAtZero: true,
      title: { display: false },
    },
  },
};

new Chart(ctx, {
  type: 'bar', // Base type (though we're using 'line' datasets)
  data: data,
  options: options,
});
