import React, { useState , useEffect, useContext} from 'react';
import { NavLink } from 'react-router-dom';

import classes from './products.module.css';
import Header from './header';
import Footer from './footer';
import { use } from 'react';
import MyContext from '../MyContext'
import ReportCard from './reportCard';

var library = '/images/products.png';
var library1 = '/images/products1.png';
var select_product = '/images/select_product.png';
var register_icon = '/images/register_icon.jpg';
var right_choice = '/images/right_choice.png';
var make_payment = '/images/make_payment.png';
var access_content = '/images/access_content.png';
var closeblack = '/images/closeblack.png';
var closewhite = '/images/closewhite.png';





function Products() {
    const [appProduct, setappProduct] = useState('');
    const {showselectors, setshowselectors} = useContext(MyContext)
    const [selectedOS, setSelectedOS] = useState('');
    cosnt 


    const products = {
        studyroom:{
            name:"Study Room",
            description: "Study Room brings the classroom home, offering live classes and monitoring student behavior to boost focus. It works fully offline, eliminating the need for subscriptions and ensuring access to education without constant internet.",
            availability: "Available on windows, coming soon (Android, Mac, ios)",
            executable: "studyroom"
        },
        threx:{},
        cxplor:{},
        tmshrm:{}
    }


    const handleChange = (event) => {
      setSelectedOS(event.target.value);
    };


    const downloadFile = (event) => {

        event.preventDefault()
        if(selectedOS == ""){
            return
        }
        // Replace with the actual URL of your file on the server
        const fileUrl = `/files/${appProduct}.pdf`;

        // Create an anchor element
        const link = document.createElement("a");
        link.href = fileUrl;

        // Set the download attribute (optional: specify a filename)
        link.setAttribute("download", `${appProduct}_${selectedOS}.pdf`);

        // Append to the document, trigger the click, then remove the element
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        setshowselectors(false)
        document.body.style.overflow = "auto";

    };


    useEffect(() => {
        // Scroll to the top of the page when the component mounts
        window.scrollTo(0, 0);
      }, []);

      const closedownload = ()=>{
        setshowselectors(false)

        document.body.style.overflow = "auto";
      }
      const opendownload = (e, appProduct)=>{
        e.preventDefault()
        setappProduct(appProduct)
        setshowselectors(true)

        document.body.style.overflow = "hidden";
      }

    return (
      <div className={`${classes.Products}`}>
        <Header isVisible={true}/>
        <div className={`${classes.productSumary}`}>
            <div className={`${classes.productSumaryInner}`}>
                {/* <h1>TMS Library</h1> */}
                <p>Our media products offer engaging self-study materials making learning more effective and enjoyable.  We do transform the learning experience.</p>
            </div>
        </div>
        <div className={`${classes.productlisting}`}>
            <h1>Our Products</h1>
            <p className={`${classes.productlistingintro}`}>Download applications and content designed just for you.</p>
            <div className={`${classes.productblocks}`}>
                <div className={`${classes.thisproduct}`}>
                    <h1>Study Room</h1>
                    <p>Study Room brings the classroom home, offering live classes and monitoring student behavior to boost focus. It works fully offline, eliminating the need for subscriptions and ensuring access to education without constant internet.</p>
                    <h5>Available on windows, coming soon (Android, Mac, ios)</h5>
                    <a href="#" onClick={(e)=>opendownload(e, "studyroom")}>click to download App</a>

                </div>
                <div className={`${classes.thisproduct}`}>
                    <h1>Threx</h1>
                    <h3>(The Remote Supervisor)</h3>
                    <p>Threx monitors student devices, allowing access to certain apps only after conditions are met, usually based on their timetable and guardian approval. It automatically restricts unauthorized apps and lets guardians remotely modify the rules for flexible control.</p>
                    <h5>Coming soon (Windows, Android, Mac, ios)</h5>

                </div>
                <div className={`${classes.thisproduct}`}>
                    <h1>cXplore</h1>
                    <h3>(career explorer)</h3>
                    <p>Threx is a powerful tool that monitors student devices, ensuring they can only access specific applications once predefined conditions are met, typically based on their timetable and guardian approval. Threx automatically restricts access to apps or functionalities that students are not permitted to use, helping maintain focus and discipline. One of its standout features is the ability for guardians to remotely modify these rules, offering flexible and seamless control over device usage.</p>
                </div>
                <div className={`${classes.thisproduct}`}>
                    <h1>TMS-HRM</h1>
                    <p>Threx is a powerful tool that monitors student devices, ensuring they can only access specific applications once predefined conditions are met, typically based on their timetable and guardian approval. Threx automatically restricts access to apps or functionalities that students are not permitted to use, helping maintain focus and discipline. One of its standout features is the ability for guardians to remotely modify these rules, offering flexible and seamless control over device usage.</p>
                </div>
                <div className={`${classes.thisproduct}`}>
                    <h1>Report card</h1>
                    <p>Threx is a powerful tool that monitors student devices, ensuring they can only access specific applications once predefined conditions are met, typically based on their timetable and guardian approval. Threx automatically restricts access to apps or functionalities that students are not permitted to use, helping maintain focus and discipline. One of its standout features is the ability for guardians to remotely modify these rules, offering flexible and seamless control over device usage.</p>
                    <button onClick={()=>setshowReportCard(true)}>click</button>
                    {showReportCard && <ReportCard/>}

                </div>
            </div>
            <div></div>
        </div>
        { showselectors && <div className={classes.selectors}>
            <form>

            <img src={closeblack} alt="close" id="close" onClick={closedownload}/>
                <h4>App: {appProduct}</h4>

                {/* <label>select operating system</label> */}
                <select value={selectedOS} onChange={handleChange}>
                    <option value="">select operating system</option>
                    <option value="windows">windows</option>
                    <option value="mac">mac</option>
                    <option value="ios">ios</option>
                    <option value="android">android</option>
                </select>
                <button onClick={downloadFile}>Download</button>
            </form>

        </div>}
        <div className={`${classes.theInside}`}>
            <div className={`${classes.notice}`}>
                <div className={`${classes.noticeInner}`}>
                    <div className={`${classes.solidBar}`}></div>
                    <h1>Every Service Comes With</h1>
                </div>
            </div>
            <div className={`${classes.noticeItems}`}>
                <div className={`${classes.noticeItem}`}>
                    <div className={`${classes.noticeItemNumber}`}>1</div>
                    <div className={`${classes.noticeItemText}`}>
                        <h1>Quality Content</h1>
                        <h3>Well explained and easy to understand materials for use.</h3>
                    </div>
                </div>
                <div className={`${classes.noticeItem}`}>
                    <div className={`${classes.noticeItemNumber}`}>2</div>
                    <div className={`${classes.noticeItemText}`}>
                        <h1>Affordable Price</h1>
                        <h3>Our services are made to be affordable by all students. One lunch meal can pay for a good period of time.</h3>
                    </div>
                </div>
                <div className={`${classes.noticeItem}`}>
                    <div className={`${classes.noticeItemNumber}`}>3</div>
                    <div className={`${classes.noticeItemText}`}>
                        <h1>Customer Service</h1>
                        <h3>If you ever run into problems, we are ever ready to provide you with assistance and proffer solutions to you.</h3>
                    </div>
                </div>
                <div className={`${classes.noticeItem}`}>
                    <div className={`${classes.noticeItemNumber}`}>4</div>
                    <div className={`${classes.noticeItemText}`}>
                        <h1>Easy User Experience</h1>
                        <h3>The interface of our products are easy to use and navigate</h3>
                    </div>
                </div>
            </div>
        </div>
        <div className={`${classes.process}`}>
            <h1>Our Process</h1>
            <div className={`${classes.processItems}`}>
                <div className={`${classes.processItem}`}>
                    <img src={select_product} alt="select_product" className={`${classes.processImage}`}/>
                    <h2>Select the product</h2>
                    <p>You can see our products here and download them to your mobile devices</p>
                </div>
                <div className={`${classes.processItem}`}>
                    <img src={register_icon} alt="register_icon" className={`${classes.processImage}`}/>
                    <h2>Select the product</h2>
                    <p>You can see our products here and download them to your movile devices</p>
                </div>
                <div className={`${classes.processItem}`}>
                    <img src={right_choice} alt="right_choice" className={`${classes.processImage}`}/>
                    <h2>Select the product</h2>
                    <p>You can see our products here and download them to your movile devices</p>
                </div>
                <div className={`${classes.processItem}`}>
                    <img src={make_payment} alt="make_payment" className={`${classes.processImage}`}/>
                    <h2>Select the product</h2>
                    <p>You can see our products here and download them to your movile devices</p>
                </div>
                <div className={`${classes.processItem}`}>
                    <img src={access_content} alt="access_content" className={`${classes.processImage}`}/>
                    <h2>Select the product</h2>
                    <p>You can see our products here and download them to your movile devices</p>
                </div>
            </div>
        </div>
        <Footer/>
      </div>
    );
  }

  export default Products;
