import React, { useState , useEffect, useContext} from 'react';
import { NavLink } from 'react-router-dom';
import classes from './contactUs.module.css';
import Header from './header';
import Footer from './footer';
var contactUs = '/images/contact_bg.jpg';
var logo = '/images/logo3.png';
var bars = '/images/bars.png';





function ContactUs() {
  const [showHeader, setShowHeader] = useState(false);
  const [showlinks, setShowLinks] = useState(false);

  const toggleLinks = () => {
    setShowLinks(!showlinks);
  };

    useEffect(() => {
        // Scroll to the top of the page when the component mounts
        window.scrollTo(0, 0);
      }, []);

        // Listen for scroll events to change the header style
  useEffect(() => {
    const handleScroll = () => {
      setShowLinks
      const scrollPosition = window.scrollY || document.documentElement.scrollTop;

      if (scrollPosition >= 50) {
        setShowHeader(true);
      } else {
        setShowHeader(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup the event listener when the component unmounts
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`${classes.ContactUs}`}>
         <Header isVisible={showHeader}/>
        <div className={`${classes.Contact_bg}`}>
          <div className={`${classes.Header}`}>
            <div className={`${classes.header_logo}`}>
                <h1>T</h1>
                <h1>M</h1>
                <h1>S</h1>
                <img src={logo} alt="logo"/>

            </div>
            <div className={`${classes.header_nav}`}>
              <NavLink to="/index">Home</NavLink>
              <NavLink to="/products">Products</NavLink>
              <NavLink to="/contactUs">Contact Us</NavLink>
            </div>
            <div className={`${classes.header_nav2}`}>
              <img src={bars} alt="bars" onClick={toggleLinks}/>
            </div>
            {/* {showlinks && <div className={`${classes.header_nav3}`}>
                <NavLink to="/index" className={`${classes.header_nav4_link}`} onClick={toggleLinks}>Home</NavLink>
                <NavLink to="/products" className={`${classes.header_nav4_link}`} onClick={toggleLinks}>Products</NavLink>
                <NavLink to="/contactUs" className={`${classes.header_nav4_link}`} onClick={toggleLinks}>Contact Us</NavLink>
            </div>} */}
          </div >
            <h1 className={`${classes.contact_bgh1}`}>Contact Us</h1>
        </div>
        {showlinks && <div className={`${classes.header_nav3}`}>
                <NavLink to="/index" className={`${classes.header_nav4_link}`} onClick={toggleLinks}>Home</NavLink>
                <NavLink to="/products" className={`${classes.header_nav4_link}`} onClick={toggleLinks}>Products</NavLink>
                <NavLink to="/contactUs" className={`${classes.header_nav4_link}`} onClick={toggleLinks}>Contact Us</NavLink>
            </div>}
        <div className={`${classes.talkwithus}`}>
            <div className={`${classes.talkwithustext}`}>
              <h1>Talk with us today</h1>
              <p>We are excited to hear from you. Ask us your questions and tell us your challenges, comments and suggestions and we will get back to you as soon as possible.</p>
              <div>
                <a href='#'>tmsedustore@gmail.com</a>
              </div>
              <div>
                <h3>+234 813 8410 085</h3>
              </div>
              <div>
                <h3>FUTMinna, Nigeria</h3>
              </div>
            </div>
            <div className={`${classes.talkwithusform}`}>
              <form action="/submit_form" method="post">
                <label for="name">Full Name</label>
                <input type="text" id="name" name="name" placeholder="Enter your full name"/>
                <label for="email">Email Address</label>
                <input type="text" id="email" name="email" placeholder="Enter email address"/>
                <label for="message">Message</label>
                <textarea id="message" name="message" placeholder="Your message here"></textarea>
                <input type="submit" value="Submit"/>
              </form>
            </div>
        </div>

        <Footer/>
    </div>
  );
}

export default ContactUs;
